import React from 'react'
// import io from 'socket.io-client'
import {List,InputItem, NavBar,Icon,Grid} from 'antd-mobile'
import {getMsgList,recvMsg,sendMsg,readMsg} from '../../redux/chat.redux'
import {connect} from 'react-redux'
import {getChatId} from '../../util'


//å‘èµ·è¿æ¥,é“¾æ¥æœåŠ¡å™¨
// const socket = io('ws://localhost:9093')
@connect(
    state => state,
    {getMsgList,recvMsg,sendMsg,readMsg}
)
class Chat extends React.Component{
    constructor(props){
        super(props)
        this.state={text:'',msg:[]}

    }
    componentDidMount(){  
        if(!this.props.chat.chatmsg.length){
            this.props.getMsgList()
            this.props.recvMsg()
        }
    }
    //é€€å‡ºè·¯ç”±æ—¶è°ƒç”¨
    componentWillUnmount(){
        //å¯¹é¢èŠå¤©å¯¹è±¡çš„id
		const to = this.props.match.params.user
		this.props.readMsg(to)
	}
    //ä¿®æ­£Gridç»„ä»¶bug
    fixCarousel(){
		setTimeout(function(){
			window.dispatchEvent(new Event('resize'))
		},0)
	}
    handleSubmit(){
        const from = this.props.user._id
		const to = this.props.match.params.user
		const msg = this.state.text
        this.props.sendMsg({from,to,msg})
		this.setState({
            text:'',
            showEmoji:false   //åˆ¤æ–­æ˜¯å¦å±•ç¤ºemojiè¡¨æƒ…
		})
    }
    render(){
        //æ·»åŠ emojiè¡¨æƒ…
        const emoji = 'ğŸ˜€ ğŸ˜ƒ ğŸ˜„ ğŸ˜ ğŸ˜† ğŸ˜… ğŸ˜‚ ğŸ˜Š ğŸ˜‡ ğŸ™‚ ğŸ™ƒ ğŸ˜‰ ğŸ˜Œ ğŸ˜ ğŸ˜˜ ğŸ˜— ğŸ˜™ ğŸ˜š ğŸ˜‹ ğŸ˜œ ğŸ˜ ğŸ˜› ğŸ¤‘ ğŸ¤— ğŸ¤“ ğŸ˜ ğŸ˜ ğŸ˜’ ğŸ˜ ğŸ˜” ğŸ˜Ÿ ğŸ˜• ğŸ™ ğŸ˜£ ğŸ˜– ğŸ˜« ğŸ˜© ğŸ˜¤ ğŸ˜  ğŸ˜¡ ğŸ˜¶ ğŸ˜ ğŸ˜‘ ğŸ˜¯ ğŸ˜¦ ğŸ˜§ ğŸ˜® ğŸ˜² ğŸ˜µ ğŸ˜³ ğŸ˜± ğŸ˜¨ ğŸ˜° ğŸ˜¢ ğŸ˜¥ ğŸ˜­ ğŸ˜“ ğŸ˜ª ğŸ˜´ ğŸ™„ ğŸ¤” ğŸ˜¬ ğŸ¤ ğŸ˜· ğŸ¤’ ğŸ¤• ğŸ˜ˆ ğŸ‘¿ ğŸ‘¹ ğŸ‘º ğŸ’© ğŸ‘» ğŸ’€ â˜ ï¸ ğŸ‘½ ğŸ‘¾ ğŸ¤– ğŸƒ ğŸ˜º ğŸ˜¸ ğŸ˜¹ ğŸ˜» ğŸ˜¼ ğŸ˜½ ğŸ™€ ğŸ˜¿ ğŸ˜¾ ğŸ‘ ğŸ™Œ ğŸ‘ ğŸ™ ğŸ‘ ğŸ‘ ğŸ‘Š âœŠ ğŸ¤˜ ğŸ‘Œ ğŸ‘ˆ ğŸ‘‰ ğŸ‘† ğŸ‘‡ âœ‹  ğŸ– ğŸ–– ğŸ‘‹  ğŸ’ª ğŸ–• âœï¸  ğŸ’… ğŸ–– ğŸ’„ ğŸ’‹ ğŸ‘„ ğŸ‘… ğŸ‘‚ ğŸ‘ƒ ğŸ‘ ğŸ‘€ '
										.split(' ')
										.filter(v=>v)
										.map(v=>({text:v}))
        console.log("chat-->props",this.props);
        //èŠå¤©æ—¶å¯¹é¢çš„id
        const userid = this.props.match.params.user
        const Item = List.Item
        const users = this.props.chat.users
        if(!users||!users[userid]){
            return null
        }
        const chatid = getChatId(userid,this.props.user._id)
        const chatmsgs = this.props.chat.chatmsg.filter(v=>v.chatid===chatid)
        return(
            <div id='chat-page'>
                <NavBar 
                    mode='dark'
                    className='chat-top-bar'
                    icon={<Icon type="left" />}
					onLeftClick={()=>{
						this.props.history.goBack()
					}}
                 >
                    {/* å¯¹æ–¹çš„id */}
                    {users[userid].name}
                </NavBar>
                <div className='chat-content'>
                    {chatmsgs.map(v=>{
                        //ç”¨æˆ·å¤´åƒ
                        const avatar = require(`../img/${users[v.from].avatar}.png`)
                        // console.log("chat-->msg",v);
                        return v.from===userid?(
                            <List key={v._id}>
                                <Item
                                    thumb={avatar}
                                >{v.content}</Item>
                            </List>
                        ):(
                            <List key={v._id}>
                                <Item 
                                   extra={<img alt='å¤´åƒ' src={avatar} />}
                                    className='chat-me'>{v.content}</Item>
                            </List>
                        )

                    })}
                </div>
                {/* è„šéƒ¨è¾“å…¥æ¡† */}
                <div className="stick-footer">
                    <List>
                        <InputItem
                            placeholder='è¯·è¾“å…¥'
                            value={this.state.text}
                            onChange={v=>{
                                this.setState({text:v})
                            }}
                            extra={
                                <div>
                                    <span
                                        style={{marginRight:15}}
										onClick={()=>{
											this.setState({
												showEmoji:!this.state.showEmoji
											})
											this.fixCarousel()
										}}
                                    >ğŸ˜ƒ</span>
                                    <span onClick={()=>this.handleSubmit()}>å‘é€</span>
                                </div>}
                        ></InputItem>
                    </List>
                    {this.state.showEmoji?<Grid
                        data={emoji}
                        columnNum={9}
                        carouselMaxRow={4}
                        isCarousel={true}
                        onClick={el=>{
                            this.setState({
                                text:this.state.text+el.text
                            })
                        }}
                    />
                    :null}
                </div>
            </div>
        )
    }
}
export default Chat
