import axios from 'axios'
import {Toast} from 'antd-mobile'  //轻提示

//拦截请求
axios.interceptors.request.use(function(config){
         Toast.loading('加载中',0)
         return config
})

//拦截响应
axios.interceptors.response.use(function(config){
         Toast.hide()
         return config
})
