// user.redux.js
import axios from 'axios'; // 用axios做请求
import {getRedirectPath} from '../utils';

// 定义常量
const REGISTER_SUCCESS = 'REGISTER_SUCCESS'; // 注册成功
const TODO_ERRSHOW = 'TODO_ERRSHOW'; // 操作失败
const GET_USER_INFO = 'GET_USER_INFO';
const toDoFail = 'toDoFail';
const LoginSuccess = 'LoginSuccess';
const LoginOutSuccess ='LoginOutSuccess';

// state初始值
const initState = {
    redirectTo: '', // 完成之后跳到哪里
    user: '', // 账号
    pwd: '', // 密码
    pwdConfirm: '', // 确认密码
    type: '', // 用户类型
    msg: '', // 错误消息
    isLogin: false // 
}

export function user(state=initState,action) {
    switch (action.type) {
       case LoginSuccess:
            return {...state, msg:'',redirectTo:getRedirectPath(action.payload),isLogin:true,...action.payload}
       case REGISTER_SUCCESS:
            return {...state, msg:'',redirectTo:getRedirectPath(action.payload),isLogin:true,...action.payload}
       case GET_USER_INFO:
           return {...state,...action.payload}
       case TODO_ERRSHOW:
            return {...state, isLogin:false, msg:action.msg}
       default:
            return state;
    }
}
function loginSuccess(obj){
	const {pwd,...data} = obj
	return {type: LoginSuccess, payload:data}
}
function loginOutSuccess(data){
    return { type:LoginOutSuccess };
}
function registerFail(msg) {
    return {
        msg,
        type: TODO_ERRSHOW
    }
}
function errorMsg(msg){
    return { 
        msg, 
        type:toDoFail 
    }
}

function registerSuccess(data) {
    return {
        data,
        type: REGISTER_SUCCESS
    }
}
export function getUserInfo(userInfo) {
    return {
        type: GET_USER_INFO,
        payload: userInfo
    }    
}
//login
export function login({user,pwd}) {
    if(!user || !pwd) {
        return errorMsg('账号密码不能为空')
    }
    return dispatch => {
        axios.post('/user/login',{user,pwd})
            .then(res => {
                if(res.status === 200 && res.data.code === 0) {
                    dispatch(loginSuccess(res.data.data))
                }else {
                    dispatch(toDoFail(res.data.msg))
                }
            })
    }
}
// register是一个action creator ，返回的action供user这个reducer使用，从而改变state
export function register({user,pwd,pwdConfirm,type}) {
    if(!user || !pwd|| !type) {
        registerFail('账号密码不能为空')
    }
    if(pwd !== pwdConfirm) {
        registerFail('两次密码不一致')
    }
    return dispatch => {
        axios.post('/user/register',{user,pwd,type})
            .then(res => {
                if(res.status === 200 && res.data.code === 0) {
                    dispatch(registerSuccess(res.data.data))
                }else {
                    dispatch(registerFail(res.data.msg))
                }
            })
    }
}
export function loginOut() {
    return dispatch => {
        axios.get('/user/loginOut')
                .then(res => {
                if (res.status === 200 && res.data.code === 0) {
                    dispatch(loginOutSuccess(res.data.data))
                } else {
                    dispatch(toDoFail(res.data.msg))
                }
            })
    }
}
